choiceListProvider.scriptText:
import jenkins.model.*
import hudson.model.*

def getAllBuildNumbers(Job job) {
    def buildNumbers = []
    (job.getBuilds()).each { build ->
        if (build.getResult() == Result.SUCCESS) {
            buildNumbers.add(build.getDisplayName())
        }
    }
    return buildNumbers.unique().sort()
}

def buildJob = Jenkins.instance.getItemByFullName('CF-Demo/build-project');
return getAllBuildNumbers(buildJob)

hudson.tasks.Shell.command:
#!/bin/bash BUILD_VERSION=$PROMOTED_VERSION
if [ ! -d ${WORKSPACE}/artifacts ]; then mkdir ${WORKSPACE}/artifacts fi
wget http://192.168.99.102:8081/nexus/service/local/repositories/releases/content/io/spring/cloud/samples/${APP_NAME}/${BUILD_VERSION}/${APP_NAME}-${BUILD_VERSION}.jar
mv ${APP_NAME}-${BUILD_VERSION}.jar ${WORKSPACE}/artifacts/${APP_NAME}-${BUILD_VERSION}.jar
if [ ! -f cf ]; then wget https://cli.run.pivotal.io/stable?release=linux32-binary -O cf-linux-amd64.tgz tar -zxvf cf-linux-amd64.tgz fi ./cf --version ./cf api --skip-ssl-validation ${CF_API_URL} ./cf login -u ${CF_USER} -p ${CF_PASSWORD} -o ${CF_ORG} -s ${CF_SPACE}
./cf create-service p-mongodb development cf-demo-mongo-service-${ENVIRONMENT} ./cf create-service p-rabbitmq standard cf-demo-rabbit-service-${ENVIRONMENT} ./cf create-service p-mysql 100mb-dev cf-demo-mysql-service-${ENVIRONMENT}
DEPLOYED_VERSION_CMD=$(CF_COLOR=false ./cf apps | grep 'cf-build-demo-' | cut -d" " -f1) DEPLOYED_VERSION="$DEPLOYED_VERSION_CMD" ROUTE_VERSION=$(echo "${BUILD_VERSION}" | cut -d"." -f1-3 | tr '.' '-') echo "Deployed Version: $DEPLOYED_VERSION" echo "Route Version: $ROUTE_VERSION"
./cf push "$APP_NAME-$BUILD_VERSION" -i 1 -m 512M -n "$APP_NAME-$ROUTE_VERSION-${ENVIRONMENT}" -d $CF_APPS_DOMAIN -p artifacts/${APP_NAME}-${BUILD_VERSION}.jar --no-manifest --no-start
./cf bind-service "$APP_NAME-$BUILD_VERSION" cf-demo-mongo-service-${ENVIRONMENT} ./cf bind-service "$APP_NAME-$BUILD_VERSION" cf-demo-rabbit-service-${ENVIRONMENT} ./cf bind-service "$APP_NAME-$BUILD_VERSION" cf-demo-mysql-service-${ENVIRONMENT}
./cf start "$APP_NAME-$BUILD_VERSION"
./cf map-route "${APP_NAME}-${BUILD_VERSION}" $CF_APPS_DOMAIN -n ${APP_NAME}-${ENVIRONMENT} ./cf scale ${APP_NAME}-${BUILD_VERSION} -i 2
if [ ! -z "$DEPLOYED_VERSION" -a "$DEPLOYED_VERSION" != "" -a "$DEPLOYED_VERSION" != "$APP_NAME-$BUILD_VERSION" ]; then echo "Performing zero-downtime cutover to $BUILD_VERSION" while read line do if [ ! -z "$line" -a "$line" != " " -a "$line" != "${APP_NAME}-${BUILD_VERSION}" ]; then echo "Scaling down, unmapping and removing $line" ./cf scale "$line" -i 1 ./cf unmap-route "$line" $CF_APPS_DOMAIN -n ${APP_NAME}-${ENVIRONMENT} ./cf delete "$line" -f else echo "Skipping $line" fi done <<< "$DEPLOYED_VERSION" fi
